//resize
const busImage = document.querySelector(".enlargeImg");
busImage.addEventListener("click", enlargeImg);
busImage.addEventListener("keydown", busImageNormal);

function enlargeImg(event) {
  busImage.style.transform = "scale(1.2)";
}

function busImageNormal(event) {
  busImage.style.transform = "scale(1)";
}

//keydown function
function closeOnEsc(event) {
  if (event.key === "Escape") {
    busImageNormal();
  }
}
document.addEventListener("keydown", closeOnEsc);

