const clickOnHeader = document.querySelector(".intro h2");
clickOnHeader.addEventListener("mouseover", newColor);
clickOnHeader.addEventListener("mouseout", stopColor);

function newColor() {
  clickOnHeader.style.color = "blue";
}

function stopColor() {
  clickOnHeader.style.color = "black";
}

//double click

const doubleClickOnImg = document.images[1];
doubleClickOnImg.addEventListener("dblclick", newImgContent);
doubleClickOnImg.addEventListener("keydown", originalImg);
console.log(doubleClickOnImg);

function newImgContent() {
  doubleClickOnImg.src =
    "data:image/jpeg;base64,";
}
function originalImg() {
  doubleClickOnImg.src = "img/adventure.jpg";
}

function closeOnEsc(event) {
  if (event.key === "Escape") {
    busImageNormal();
  }
}
document.addEventListener("keydown", originalImg);

//keyup
const selectBtn = document.querySelector(".btn");
document.addEventListener("keyup", (event) => {
  if (event.key === "b") {
    selectBtn.style.backgroundColor = "blue";
  }
});

//mousedown and mouseup
const selectButtons = document.querySelector(".destination .btn");
console.log(selectButtons);
selectButtons.addEventListener("mousedown", changeScale);
selectButtons.addEventListener("mouseup", scaleDown);

function changeScale() {
  selectButtons.style.transform = "scale(2)";
}

function scaleDown() {
  selectButtons.style.transform = "scale(1)";
}

window.addEventListener("load", () => {
  alert("We are up and running, Captain");
});

//mouseleave
const imgBlur = document.querySelector(".img-content");

imgBlur.addEventListener("mouseleave", function (e) {
  e.target.style.opacity = 0.33;
  e.stopPropagation();
});

//context menu
const btnThree = document.querySelector(".intro p");
btnThree.addEventListener("contextmenu", (event) => {
  btnThree.style.backgroundColor = "red";
  event.stopPropagation();
});

